function printDirectoryStructure(data) {
  // –ò—Å–ø–æ–ª—å–∑—É–µ–º —Å—Ç–µ–∫ –¥–ª—è –∏—Ç–µ—Ä–∞—Ç–∏–≤–Ω–æ–≥–æ –æ–±—Ö–æ–¥–∞ —Å—Ç—Ä—É–∫—Ç—É—Ä—ã
  const stack = [{ data, level: 0 }];

  while (stack.length > 0) {
    // –ò–∑–≤–ª–µ–∫–∞–µ–º —Ç–µ–∫—É—â–∏–π —ç–ª–µ–º–µ–Ω—Ç –∏–∑ —Å—Ç–µ–∫–∞
    const { data, level } = stack.pop();

    // –í—ã–≤–æ–¥–∏–º –∏–º—è —Å —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É—é—â–∏–º –æ—Ç—Å—Ç—É–ø–æ–º
    console.log(" ".repeat(level * 2) + data.name);

    // –ï—Å–ª–∏ –µ—Å—Ç—å –¥–µ—Ç–∏, –¥–æ–±–∞–≤–ª—è–µ–º –∏—Ö –≤ —Å—Ç–µ–∫ –≤ –æ–±—Ä–∞—Ç–Ω–æ–º –ø–æ—Ä—è–¥–∫–µ
    if (data.children && Array.isArray(data.children)) {
      for (let i = data.children.length - 1; i >= 0; i--) {
        stack.push({
          data: data.children[i],
          level: level + 1,
        });
      }
    }
  }
}

