function PromiseAny (arr) {

    if (arr.length === 0) {
        return Promise.reject(new AggregateError([], '–í—Å–µ –ø—Ä–æ–º–∏—Å—ã –æ—Ç–∫–ª–æ–Ω–µ–Ω—ã'))
    }

    return new Promise((resolve, reject) => {
        const errors = []
        let rejectedCount = 0
        arr.forEach((promise, index) => {
            Promise.resolve(promise)
                .then(resolve)
                .catch((error) => {
                    errors[index] = error
                    rejectedCount += 1

                    if (rejectedCount === arr.length) {
                        reject(new AggregateError(errors, '–í—Å–µ –ø—Ä–æ–º–∏—Å—ã –æ—Ç–∫–ª–æ–Ω–µ–Ω—ã'))
                    }
            })
        })
    })
}
