const nominals = [5000, 1000, 500, 100, 50];  

const limits = {50:10,100:10,500:10,1000:10,5000:10};  

function atmGiveMoneyObj(amount, limits) {  

    if (amount % 50 !== 0) {  
        throw new Error("Incorrect value");  
    }  

    let remaining = amount;  
    const result = {50:0,100:0,500:0,1000:0,5000:0};  

    for (const nominal of nominals) {  
        if (remaining === 0) break;  
        const needed = Math.floor(remaining / nominal);  
        if (needed > 0) {  
            const available = limits[nominal] || 0;  
            const take = Math.min(needed, available);  
            if (take > 0) {  
                remaining -= take * nominal;  
                limits[nominal] = available - take;  
                result[nominal] = take;  
            }  
        }    }  
    if (remaining > 0) {  
        throw new Error("Not enough money");  
    }  

    return result;  
}  



