    ticketMap[ticket.from].push(ticket);
  });

  // –ò—â–µ–º –≥–æ—Ä–æ–¥ –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–∏—è (–≥–æ—Ä–æ–¥, –∏–∑ –∫–æ—Ç–æ—Ä–æ–≥–æ –Ω–µ—Ç —Ä–µ–π—Å–æ–≤)
  let currentCity = null;
  Object.keys(ticketMap).forEach((city) => {
    // –ï—Å–ª–∏ –∏–∑ —ç—Ç–æ–≥–æ –≥–æ—Ä–æ–¥–∞ –Ω–µ—Ç —Ä–µ–π—Å–æ–≤, —ç—Ç–æ –Ω–∞—à —Å—Ç–∞—Ä—Ç–æ–≤—ã–π –≥–æ—Ä–æ–¥
    if (!tickets.some((ticket) => ticket.to === city)) {
