function fibonacci(n) {
  // –û–±—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º –±–∞–∑–æ–≤—ã–µ —Å–ª—É—á–∞–∏:
  // f(1) = 0, f(2) = 1
  if (n <= 1) return 0;
  if (n === 2) return 1;
  // prev ‚Äî –ø—Ä–µ–¥—ã–¥—É—â–µ–µ —á–∏—Å–ª–æ (f(1))
  // curr ‚Äî —Ç–µ–∫—É—â–µ–µ —á–∏—Å–ª–æ (f(2))
  let prev = 0;
  let curr = 1;
  // –ù–∞—á–∏–Ω–∞–µ–º —Å f(3) –∏ –∏–¥—ë–º –¥–æ f(n)
  for (let i = 3; i <= n; i++) {
    // –í—ã—á–∏—Å–ª—è–µ–º —Å–ª–µ–¥—É—é—â–µ–µ —á–∏—Å–ª–æ –§–∏–±–æ–Ω–∞—á—á–∏
    let next = prev + curr;

    // –°–¥–≤–∏–≥–∞–µ–º –∑–Ω–∞—á–µ–Ω–∏—è –≤–ø–µ—Ä—ë–¥:
    // —Ç–µ–∫—É—â–µ–µ —Å—Ç–∞–Ω–æ–≤–∏—Ç—Å—è –ø—Ä–µ–¥—ã–¥—É—â–∏–º, –∞ –Ω–æ–≤–æ–µ ‚Äî —Ç–µ–∫—É—â–∏–º
    prev = curr;
    curr = next;
  }
  // –ü–æ—Å–ª–µ –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è —Ü–∏–∫–ª–∞ curr —Å–æ–¥–µ—Ä–∂–∏—Ç f(n)
  return curr;
}

