function countPairsWithSum() {
  const array = Array.from({ length: 10 }, () => Math.floor(Math.random() * 11));
  const target = 10;

  // –°—á–∏—Ç–∞–µ–º –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –≤—Ö–æ–∂–¥–µ–Ω–∏–π –∫–∞–∂–¥–æ–≥–æ —á–∏—Å–ª–∞
  const frequency = {};
  let count = 0;

  for (const num of array) {
    const complement = target - num;
    if (frequency[complement]) {
      count += frequency[complement];
    }

    frequency[num] = (frequency[num] || 0) + 1;
  }

  console.log(array, count);
  return count;
}

