type GroupByDate = <T extendes { tasks: { date: string }[] }[]> (arr: T) => [string, number][]



const groupByDate: GroupByDate = (arr) => {
  const map = {};
  arr.forEach((el) => {
    el.tasks.forEach((task) => {
      const date = task.date;

      if (map[date]) {
        map[date]++
      } else {
        map[date] = 1
      }
    })
  })

  const sortedDates = Object.keys(map).sort((a, b) => new Date(a) - new Date(b)).reduce((acc, val) => {
    acc[val] = map[val]
    return acc;
  }, {});

  return Object.entries(sortedDates)
}

