  return (
    <>
      <div style={{ height: '25vh', overflow: 'hidden' }}>
        <div>Scroll: {scroll}</div>
        <ul>
          {items.map((item) => (
            <li key={item} style={{ marginTop: '8px', marginBottom: '8px' }}>
              {item}
            </li>
          ))}
        </ul>
      </div>
    </>
  );
};

