function topKFrequent(nums, k) {
  const frequencyMap = {}; // –ò—Å–ø–æ–ª—å–∑—É–µ–º –æ–±—ä–µ–∫—Ç –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è —á–∞—Å—Ç–æ—Ç—ã

  // –ü–æ–¥—Å—á–µ—Ç —á–∞—Å—Ç–æ—Ç—ã –∫–∞–∂–¥–æ–≥–æ —á–∏—Å–ª–∞
  nums.forEach(num => {
    frequencyMap[num] = (frequencyMap[num] || 0) + 1;
  });

  // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –æ–±—ä–µ–∫—Ç –≤ –º–∞—Å—Å–∏–≤ –ø–∞—Ä [—á–∏—Å–ª–æ, —á–∞—Å—Ç–æ—Ç–∞], –∑–∞—Ç–µ–º —Å–æ—Ä—Ç–∏—Ä—É–µ–º –ø–æ —á–∞—Å—Ç–æ—Ç–µ
  const sortedNums = Object.entries(frequencyMap).sort((a, b) => b[1] - a[1]);

  // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –ø–µ—Ä–≤—ã–µ K —á–∏—Å–µ–ª
  return sortedNums.slice(0, k).map(entry => entry[0]);
}

