function useStateWithHistory(initial) {
  const [state, setStateFunc] = useState(() => initial);
  const history = useRef([initial]);
  let currentIndex = useRef(0);

  const setState = useCallback((state) => {
    history.current.push(state);
    setStateFunc(state);
    currentIndex.current = history.current.length - 1;
  }, []);

  const goBack = useCallback(() => {
    if (currentIndex.current <= 0) {
      return;
    }
    currentIndex.current -= 1;
    setStateFunc(history.current[currentIndex.current]);
  }, []);

  const goForward = useCallback(() => {
    if (currentIndex.current >= history.current.length - 1) {
      return;
    }
    currentIndex.current += 1;
    setStateFunc(history.current[currentIndex.current]);
  }, []);

  return [state, setState, goBack, goForward, history.current];
}


