// –§—É–Ω–∫—Ü–∏—è debounce –æ—Å—Ç–∞–µ—Ç—Å—è –Ω–µ–∏–∑–º–µ–Ω–Ω–æ–π
const debounce = (callback, delay) => {
  let timeoutId;
  return (...args) => {
    clearTimeout(timeoutId);
    timeoutId = setTimeout(() => {
      callback(...args);
    }, delay);
  };
};

export function App() {
  const [search, setSearch] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [peoples, setPeoples] = useState([]);

  useEffect(() => {
    if (search) {
      setIsLoading(true);
      getPeople(search, 1)
        .then(data => {
          setPeoples(data.results);
        })
        .catch(error => {
            console.error(error);
        })
        .finally(() => {
          setIsLoading(false);
        })
    } else {
      setPeoples([]);
    }
  }, [search]);

  const handleSearch = event => {
    setSearch(event.target.value);
  };

  const debouncedSearch = debounce(handleSearch, 500);

  return (
    <div>
      <input type='text' onChange={debouncedSearch} placeholder='–ü–æ–∏—Å–∫' />
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <ul>
          {peoples.map(person => (
            <li key={person.url}>{person.name}</li>
          ))}
        </ul>
      )}
    </div>
  );
}

