    let left = 0;
    let right = arr.length - 1;
    let leftSum = 0;
    let rightSum = 0;
    let maxLeftSum = 0;

    while (left <= right) {
        if (leftSum <= rightSum) {
            leftSum += arr[left];
            left++;
        } else {
            rightSum += arr[right];
            right--;
        }

        if (leftSum === rightSum) {
            maxLeftSum = Math.max(maxLeftSum, leftSum);
        }
    }

