function minMoves(lock, password) {
  const rows = lock.length; // –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —Å—Ç—Ä–æ–∫
  const cols = lock[0].length; // –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —Å—Ç–æ–ª–±—Ü–æ–≤

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø—Ä–µ–æ–±—Ä–∞–∑–æ–≤–∞–Ω–∏—è —á–∏—Å–ª–∞ –≤ –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã (row, col)
  function getCoordinates(num) {
    const row = Math.floor((num - 1) / cols);
    const col = (num - 1) % cols;
    return { row, col };
  }

  let moves = 0;

  // –ü—Ä–æ—Ö–æ–¥–∏–º –ø–æ –∫–∞–∂–¥–æ–º—É —á–∏—Å–ª—É –≤ –ø–∞—Ä–æ–ª–µ
  for (let i = 1; i < password.length; i++) {
    const start = getCoordinates(password[i - 1]);
    const end = getCoordinates(password[i]);

    // –†–∞—Å—Å—á–∏—Ç—ã–≤–∞–µ–º —Ä–∞–∑–Ω–∏—Ü—É –ø–æ —Å—Ç—Ä–æ–∫–∞–º –∏ —Å—Ç–æ–ª–±—Ü–∞–º
    const rowDiff = Math.abs(start.row - end.row);
    const colDiff = Math.abs(start.col - end.col);

    // –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –¥–≤–∏–∂–µ–Ω–∏–π —Ä–∞–≤–Ω–æ –º–∞–∫—Å–∏–º–∞–ª—å–Ω–æ–π —Ä–∞–∑–Ω–∏—Ü–µ
    moves += Math.max(rowDiff, colDiff);
  }

  return moves;
}

