import "./styles.css";
import React from "react";

interface DataItem {
  id: number;
  name: string;
  category: string;
}

interface Category {
  id: number;
  name: string;
  localized_ru: string;
  localized_en: string;
}

async function getData(): Promise<DataItem[]> {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve([
        { id: 1, name: "potato", category: "vegetable" },
        { id: 2, name: "cucumber", category: "vegetable" },
        { id: 3, name: "orange", category: "fruit" },
        { id: 4, name: "pumpkin", category: "vegetable" },
        { id: 5, name: "melon", category: "fruit" },
      ]);
    }, 2000);
  });
}

async function getCategories(): Promise<Category[]> {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve([
        {
          id: 1,
          name: "vegetable",
          localized_ru: "–û–≤–æ—â",
          localized_en: "Vegetable",
        },
        { id: 2, name: "fruit", localized_ru: "–§—Ä—É–∫—Ç", localized_en: "Fruit" },
      ]);
    }, 2000);
  });
}

export default function App() {
  const [items, setItems] = React.useState<DataItem[]>([]);
  const [category, setCategory] = React.useState<Category[]>([]);
  const [isLoading, setIsLoading] = React.useState(false);

  React.useEffect(() => {
    setIsLoading(true);
    Promise.all([getData(), getCategories()])
      .then(([data, categories]) => {
        setItems(data);
        setCategory(categories);
      })
      .catch(console.error)
      .finally(() => setIsLoading(false));
  }, []);

  const getCategoryName = (categoryName: string) => {
    const foundCategory = category.find(
      (category) => category.name === categoryName
    );
    return foundCategory ? foundCategory.localized_ru : "–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–æ";
  };

  if (isLoading) return <p>Loading...</p>;

  return (
    <div className="App">
      {items.map((item) => (
        <React.Fragment key={item.id}>
          <p>
            {item.name} - {getCategoryName(item.category)}
          </p>
        </React.Fragment>
      ))}
    </div>
  );
}

