class Emitter {
    const switchedEvents = {}
    on(event: string, handler: (data: unknown[]) => void) {
        this.switchedEvents[event] = [...this.switchedEvents[event], handler]
    }

    emit(event: string, ...rest: unknown[]) {
        this.switchedEvents[event].forEach((handler) => {
            handler(...rest)
        })
    }

    off(event: string, handler: (data: unknown[]) => void) {
        this.switchedEvents[event] = this.switchedEvents[event].filter((fn) => fn !== handler)
    }
}

