function adjustForAntimeridian(line) {
  const adjustedLine = [...line];

  for (let i = 1; i < adjustedLine.length; i++) {
    const startLng = adjustedLine[i - 1][0];
    const endLng = adjustedLine[i][0];

    if (endLng - startLng >= 180) {
      adjustedLine[i][0] -= 360;
    } else if (endLng - startLng < -180) {
      adjustedLine[i][0] += 360;
    }
  }

  return adjustedLine;
}

