import * as React from "react";
import { render } from "react-dom";
import classNames from "classnames";

import "./styles.css";

type CustomInputProps = Omit<
  React.InputHTMLAttributes<HTMLInputElement>,
  "type"
> & {
  hint?: React.ReactNode;
};

const CustomInput = ({
  placeholder,
  className,
  hint,
  value: externalValue,
  ...props
}: CustomInputProps) => {
  const [inputValue, setInputValue] = React.useState(externalValue || "");

  const handleInputChange = (event) => {
    const newValue = event.target.value;
    setInputValue(newValue);
  };

  return (
    <>
      <input
        className={`custom-input ${className}`}
        placeholder={placeholder}
        value={inputValue}
        onChange={handleInputChange}
        {...props}
      />
      {hint && <>{hint}</>}
      <div>{inputValue.length}</div>
    </>
  );
};

function App() {
  const [step, setStep] = React.useState("1");

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setStep(e.target.value);
  };

  const handleAddOne = () => {
    setStep((prev) => prev + "1");
  };

  return (
    <div className="app">
      <div>{step}</div>

      <button onClick={handleAddOne}>–£–≤–µ–ª–∏—á–∏—Ç—å —à–∞–≥</button>

      <CustomInput
        value={step}
        onChange={handleChange}
        hint={<div>–í–≤–µ–¥–∏—Ç–µ —Ä–∞–∑–º–µ—Ä —à–∞–≥–∞</div>}
      />

      <CustomInput placeholder="–í–≤–µ–¥–∏—Ç–µ –∏–º—è" className="border-orange " />
    </div>
  );
}

const rootElement = document.getElementById("root");
render(<App />, rootElement);

