const canSplit = (arr, groupSize) => {
    if(arr.length % groupSize !== 0) return false

    const unique = {}

    arr.sort((a, b) => a - b)

    for(let i = 0; i < arr.length; i += 1) {
        if (arr[i] > 0) {
            let next = arr[i] + 1;
            arr[i] = -1;
            let currentGroupSize = 1;
            let j = i + 1;
            while (j < arr.length && currentGroupSize < groupSize) {
                if (arr[j] === next) {
                    next = arr[j] + 1;
                    arr[j] = -1;
                    currentGroupSize += 1;
                }
                j += 1;
            }

            if (currentGroupSize !== groupSize) return false
        }
    }
    return true
}

