const groupDataBySkill = (data) => {
    return data.reduce((total, item) => {

        if (!total[item.skill]){
            total[item.skill] = []
        }
        total[item.skill].push(item)

        total[item.skill] = total[item.skill] ??= [];
        total[item.skill] = [...total[item.skill], item];

        return total;
    }, {});
};

const main = async () => {
    try {
        const data = await fetchData();
        const mappedData = groupDataBySkill(data);
        console.log(mappedData);
    } catch(err) {
        console.error('Error: ', err)
    }
};
main();

