import React, { useState, useEffect } from "react";
import { Button, Container } from "./style";

const App = () => {
  const [isDisabled, setIsDisabled] = useState(false);
  const [count, setCount] = useState(0);

  let timer;

  const onResentEmail = () => {
    setIsDisabled(true);
    setCount(5);
    timer = setInterval(() => {
      setCount((prev) => prev - 1);
    }, 1000);
    console.log("resend email");
  };

  useEffect(() => {
    if (count <= 0) {
      setIsDisabled(false);
      clearInterval(timer);
    }
  }, [timer, count]);

  return (
    <Container>
      <Button onClick={onResentEmail} disabled={isDisabled}>
        Resend Email
      </Button>
      {isDisabled && <div>{count}</div>}
    </Container>
  );
};

export default App;

