export function runWithLimit<RequestData, Response>({
  callback,
  data,
  limit,
}: Props<RequestData, Response>): Promise<Response[]> {
  // –ú–∞—Å—Å–∏–≤ –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤ –≤—ã–ø–æ–ª–Ω–µ–Ω–∏—è callback
  const result: Response[] = [];

  // –û—á–µ—Ä–µ–¥—å –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è –∏–Ω–¥–µ–∫—Å–æ–≤ –≤—ã–ø–æ–ª–Ω—è–µ–º—ã—Ö –∑–∞–¥–∞—á
  const queue: Promise<void>[] = [];

  // –¢–µ–∫—É—â–∏–π –∏–Ω–¥–µ–∫—Å –æ–±—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º—ã—Ö –¥–∞–Ω–Ω—ã—Ö
  let currentIndex = 0;

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –≤—ã–ø–æ–ª–Ω–µ–Ω–∏—è –∑–∞–¥–∞—á —Å —É—á–µ—Ç–æ–º –ª–∏–º–∏—Ç–∞
  const worker = (): Promise<void> => {
    // –ï—Å–ª–∏ –≤—Å–µ –∑–∞–¥–∞—á–∏ –æ–±—Ä–∞–±–æ—Ç–∞–Ω—ã, –∑–∞–≤–µ—Ä—à–∞–µ–º –≤—ã–ø–æ–ª–Ω–µ–Ω–∏–µ
    if (currentIndex >= data.length) {
      return Promise.resolve();
    }

    // –ë–µ—Ä–µ–º –∏–Ω–¥–µ–∫—Å —Ç–µ–∫—É—â–µ–π –∑–∞–¥–∞—á–∏
    const index = currentIndex;
    currentIndex++;

    // –í—ã–ø–æ–ª–Ω—è–µ–º callback –∏ —Å–æ—Ö—Ä–∞–Ω—è–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç
    const task = callback(data[index])
      .then((response) => {
        result[index] = response; // –°–æ—Ö—Ä–∞–Ω—è–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç –Ω–∞ –ø—Ä–∞–≤–∏–ª—å–Ω—É—é –ø–æ–∑–∏—Ü–∏—é
      })
      .catch((error) => {
        console.error(`–û—à–∏–±–∫–∞ –ø—Ä–∏ –≤—ã–ø–æ–ª–Ω–µ–Ω–∏–∏ –∑–∞–ø—Ä–æ—Å–∞: ${error}`);
      });

    // –î–æ–±–∞–≤–ª—è–µ–º —Ç–µ–∫—É—â—É—é –∑–∞–¥–∞—á—É –≤ –æ—á–µ—Ä–µ–¥—å
    const taskPromise = task.then(() => {
      // –ü–æ—Å–ª–µ –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è –∑–∞–¥–∞—á–∏ –∑–∞–ø—É—Å–∫–∞–µ–º –Ω–æ–≤—É—é (—Ä–µ–∫—É—Ä—Å–∏–≤–Ω—ã–π –≤—ã–∑–æ–≤)
      return worker();
    });

    queue.push(taskPromise);

    return taskPromise;
  };

  // –ó–∞–ø—É—Å–∫–∞–µ–º –≤—ã–ø–æ–ª–Ω–µ–Ω–∏–µ –ø–µ—Ä–≤—ã—Ö `limit` –∑–∞–¥–∞—á
  const initialWorkers = Array.from({ length: Math.min(limit, data.length) }, () =>
    worker()
  );

  // –î–æ–∂–∏–¥–∞–µ–º—Å—è –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è –≤—Å–µ—Ö –∑–∞–¥–∞—á –∏ –≤–æ–∑–≤—Ä–∞—â–∞–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç
  return Promise.all(initialWorkers).then(() => result);
}


