export function nodeCleaner(rootNode: NodeItem | null): NodeItem | null {
  // –ï—Å–ª–∏ –∫–æ—Ä–Ω–µ–≤–æ–π —É–∑–µ–ª –æ—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç, –≤–æ–∑–≤—Ä–∞—â–∞–µ–º null
  if (!rootNode) return null;

  // –§–∏–ª—å—Ç—Ä—É–µ–º –¥–æ—á–µ—Ä–Ω–∏–µ —É–∑–ª—ã, –æ—Å—Ç–∞–≤–ª—è—è —Ç–æ–ª—å–∫–æ —Ç–µ, –∫–æ—Ç–æ—Ä—ã–µ –¥–æ–ª–∂–Ω—ã –æ—Å—Ç–∞—Ç—å—Å—è –≤ –¥–µ—Ä–µ–≤–µ
  rootNode.children = rootNode.children
    .map(child => nodeCleaner(child)) // –†–µ–∫—É—Ä—Å–∏–≤–Ω–æ –æ—á–∏—â–∞–µ–º –∫–∞–∂–¥—ã–π –¥–æ—á–µ—Ä–Ω–∏–π —É–∑–µ–ª
    .filter(child => child !== null); // –£–¥–∞–ª—è–µ–º —É–∑–ª—ã, –∫–æ—Ç–æ—Ä—ã–µ —Å—Ç–∞–ª–∏ null

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –¥–æ–ª–∂–µ–Ω –ª–∏ —Å–∞–º —É–∑–µ–ª –æ—Å—Ç–∞—Ç—å—Å—è –≤ –¥–µ—Ä–µ–≤–µ
  if (!rootNode.alive && rootNode.children.length === 0) {
    return null; // –ï—Å–ª–∏ —É–∑–µ–ª —Å–∞–º "–º–µ—Ä—Ç–≤" –∏ —É –Ω–µ–≥–æ –Ω–µ—Ç –∂–∏–≤—ã—Ö –¥–µ—Ç–µ–π, –æ–Ω —É–¥–∞–ª—è–µ—Ç—Å—è
  }

  return rootNode; // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –æ—á–∏—â–µ–Ω–Ω–æ–µ –¥–µ—Ä–µ–≤–æ
}

