 function letterCounter (str) { // O(nLogn)
  const hash = {}
  let letter = ''
  let maxNumber = 0
  const keys = Object.keys(hash).sort() // O(nLogn)
  for (const el of str) { // O(n)
    hash[el] ? hash[el] += 1 : hash[el] = 1
    if (hash[el] === maxNumber) {
