        if (isBadVersion(middle)) {
            // –≤ –ª–µ–≤–æ–π –ø–æ–ª–æ–≤–∏–Ω–µ –∏—Å–∫–∞—Ç—å
            pointer2 = middle
            averagePointer = middle
        } else {
            pointer1 = middle
        }    
    }
  return averagePointer
}



