function toCamelCase(str) {
  // –ó–∞–º–µ–Ω—è–µ–º –≤—Å–µ –ø–æ–¥—á–µ—Ä–∫–∏–≤–∞–Ω–∏—è –Ω–∞ –¥–µ—Ñ–∏—Å—ã
  str = str.replace(/_/g, '-');

  // –†–∞–∑–¥–µ–ª—è–µ–º —Å—Ç—Ä–æ–∫—É –Ω–∞ —á–∞—Å—Ç–∏ –ø–æ –¥–µ—Ñ–∏—Å–∞–º
  const parts = str.split('-');

  // –û—Å—Ç–∞–≤–ª—è–µ–º –ø–µ—Ä–≤—É—é —á–∞—Å—Ç—å –≤ –∏—Å—Ö–æ–¥–Ω–æ–º —Ä–µ–≥–∏—Å—Ç—Ä–µ
  const firstPart = parts[0];

  // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –∫–∞–∂–¥—É—é –ø–æ—Å–ª–µ–¥—É—é—â—É—é —á–∞—Å—Ç—å, —Å–¥–µ–ª–∞–≤ –ø–µ—Ä–≤—É—é –±—É–∫–≤—É –∑–∞–≥–ª–∞–≤–Ω–æ–π –∏ –æ—Å—Ç–∞–ª—å–Ω—ã–µ —Å—Ç—Ä–æ—á–Ω—ã–º–∏
  const restParts = parts.slice(1).map(part => part.charAt(0).toUpperCase() + part.slice(1).toLowerCase());

  // –û–±—ä–µ–¥–∏–Ω—è–µ–º –≤—Å–µ —á–∞—Å—Ç–∏ –≤ –æ–¥–Ω—É —Å—Ç—Ä–æ–∫—É
  return firstPart + restParts.join('');
}

