import { useState, useEffect, useCallback } from 'react';

function useOperationQuery(queryParams) {
  const [data, setData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isError, setIsError] = useState(false);
  const [error, setError] = useState(null);

  const fetchData = useCallback(async (params, abortSignal) => {
    setIsError(false);
    setError(null);
    setIsLoading(true);

    try {
      const response = await fetch('/get', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(params),
        signal: abortSignal,
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const result = await response.json();
      setData(result);
    } catch (e) {
      if (e.name !== 'AbortError') { // –ò–≥–Ω–æ—Ä–∏—Ä—É–µ–º –æ—à–∏–±–∫–∏ –æ—Ç–º–µ–Ω—ã
        setIsError(true);
        setError(e);
      }
    } finally {
      setIsLoading(false);
    }
  }, []);

  useEffect(() => {
    const abortController = new AbortController();

    fetchData(queryParams, abortController.signal);

    return () => {
      abortController.abort();
    };
  }, [queryParams, fetchData]);

  return { data, isLoading, isError, error };
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
