    }

    if (store.count > 5) {
      alert("–†–µ–π—Ç–∏–Ω–≥ –Ω–µ –º–æ–∂–µ—Ç –±—ã—Ç—å –≤—ã—à–µ 5");
      return;
    }
    setStore(store);
  };

  const findUserById = (id) => {
    return store.users.find((val) => val.id === id)?.name;
  };

  // –†–µ–Ω–¥–µ—Ä —Å–ø–∏—Å–∫–∞
  const renderItem = (item) => {
    return (
      <div class="post">
        <div class="title">{item.title}</div>
        <div class="user">–ê–≤—Ç–æ—Ä: {findUserById(item.userId)}</div>
      </div>
    );
  };

  return (
    <div className="App">
      <div className="info">–í—ã —Å–º–æ—Ç—Ä–∏—Ç–µ —ç—Ç—É —Å—Ç—Ä–∞–Ω–∏—Ü—É: {time} —Å–µ–∫—É–Ω–¥</div>
      <div className="raiting">
        <button onClick={clickAdd.bind(this, false)}>-</button>
        –û—Ü–µ–Ω–∏—Ç–µ –ø–æ–¥–±–æ—Ä–∫—É –ø–æ—Å—Ç–æ–≤ (–æ—Ç 0 –¥–æ 5): <b>{store.count}</b>
        <button onClick={clickAdd}>+</button>
