function getTopCitiesByPopulation(data) {
    // –ì—Ä—É–ø–ø–∏—Ä–æ–≤–∫–∞ –≥–æ—Ä–æ–¥–æ–≤ –ø–æ —Å—Ç—Ä–∞–Ω–∞–º
    const groupedByCountry = data.reduce((acc, item) => {
        if (!acc[item.country]) {
            acc[item.country] = [];
        }
        acc[item.country].push(item);
        return acc;
    }, {});

    // –ü–æ–ª—É—á–µ–Ω–∏–µ —Ç–æ–ø-3 –≥–æ—Ä–æ–¥–æ–≤ –ø–æ –Ω–∞—Å–µ–ª–µ–Ω–∏—é –¥–ª—è –∫–∞–∂–¥–æ–π —Å—Ç—Ä–∞–Ω—ã
    const topCitiesByCountry = {};

    for (const country in groupedByCountry) {
        topCitiesByCountry[country] = groupedByCountry[country]
            .sort((a, b) => b.population - a.population)
            .slice(0, 3);
    }

    return topCitiesByCountry;
}

