function App() {
  const getEmployees = () => {
    return Array.from({ length: 50 }, (element, index) => ({
      id: index,
      name: 'Employee ' + index,
      salary: 1000 + (Math.random() * 50).toFixed(0),
    }));
  };

  const [employees] = useState(getEmployees());

  const handleRemove = (index) => {
    employees.splice(index, 1);
  };

  const RemoveButton = (props) => {
    let { onClick } = props;

    return <div onClick={onClick}>Remove</div>;
  };

  return (
    <div className="wrapper">
      <h1> Current employees:</h1>

      {employees.map((value, index) => (
        <div className="card" key={value.salary}>
          <div>
            <div>name: {value.name} </div>
            <div>salary: {value.salary}</div>
          </div>

          <RemoveButton onClick={() => handleRemove(index)} />
        </div>
      ))}
    </div>
  );
}
ReactDOM.render(<App />,
document.getElementById("root"))

