import React, { useState } from "https://esm.sh/react@18";
import ReactDOM from "https://esm.sh/react-dom@18";

function App() {
  const getEmployees = () => {
    return Array.from({ length: 50 }, (element, index) => ({
      id: index,
      name: 'Employee ' + index,
      salary: 1000 + (Math.random() * 50).toFixed(0),
    }));
  };

  const [employees, setEmployees] = useState(() => getEmployees());

  const handleRemove = (index) => {
    setEmployees(employees.filter(item => item.id !== index))
  };

  const RemoveButton = ({onClick}) => {
    return <button onClick={onClick}>Remove</button>;
  };

  return (
    <div className="wrapper">
      <h1> Current employees:</h1>

      {employees.map((value, index) => (
        <div className="card" key={value.id}>
          <div>
            <div>name: {value.name} </div>
            <div>salary: {value.salary}</div>
          </div>

          <RemoveButton onClick={() => handleRemove(value.id)} />
        </div>
      ))}
    </div>
  );
}

ReactDOM.render(<App />,
document.getElementById("root"))

