import React, { useState, useEffect } from 'react';

const TimeTracker = () => {
  const [secondsSpent, setSecondsSpent] = useState(0);

  useEffect(() => {
    // –ü–æ–ª—É—á–∞–µ–º –≤—Ä–µ–º—è –Ω–∞—á–∞–ª–∞ —Å–µ—Å—Å–∏–∏ –∏–ª–∏ —É—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –µ–≥–æ, –µ—Å–ª–∏ –æ–Ω–æ –µ—â—ë –Ω–µ –∑–∞–¥–∞–Ω–æ
    const startTime = localStorage.getItem('startTime') || Date.now();
    localStorage.setItem('startTime', startTime);

    // –ò–Ω—Ç–µ—Ä–≤–∞–ª –¥–ª—è –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è –≤—Ä–µ–º–µ–Ω–∏, –ø—Ä–æ–≤–µ–¥—ë–Ω–Ω–æ–≥–æ –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü–µ
    const intervalId = setInterval(() => {
      const currentTime = Date.now();
      const timeElapsed = Math.floor((currentTime - startTime) / 1000);
      setSecondsSpent(timeElapsed);
    }, 1000);

    return () => {
      clearInterval(intervalId);
      // –°–æ—Ö—Ä–∞–Ω—è–µ–º —Ç–µ–∫—É—â–µ–µ –≤—Ä–µ–º—è, –∫–æ–≥–¥–∞ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å —É—Ö–æ–¥–∏—Ç —Å–æ —Å—Ç—Ä–∞–Ω–∏—Ü—ã
      const endTime = Date.now();
      const totalSpent = endTime - parseInt(startTime);
      localStorage.setItem('totalTimeSpent', totalSpent);
    };
  }, []);

  return (
    <div>
      <h1>Time You've Spent on This Page</h1>
      <p>{secondsSpent} seconds</p>
    </div>
  );
}

export default TimeTracker;


