const useDebounce = (value, delay) => {
  const [debounceValue, setDebounceValue] = useState(value);
  React.useEffect(() => {
    const timer = setTimeout(() => setDebounceValue(value), delay);
    return () => clearTimeout(timer);
  }, [value]);

  return debounceValue;
};

