  const fetchBreeds = async () => {
    try {
      const response = await fetch(DOG_BREEDS_API);
      if (!response.ok) throw new Error(`–û—à–∏–±–∫–∞: ${response.status}`);
      const { data } = await response.json();
      setDogBreeds(data);
    } catch (err) {
