export const AppTimer = () => {
    let currency = '¬†$';
    const [timer, setTimer] = useState(10);

    const onDecrease = () => {
        if (counter > 0) {
            counter--;
            setTimer(counter);
        }
    };

    useLayoutEffect(() => {
        if (!started) {
            setInterval(onDecrease, 1000);
        }

