const getTransposedMatrix = (matrix) => {
  const result = [];
  const rowCount = matrix.length;
  const colCount = matrix[0].length;
  let newArr = [];
  let currentCol = 0
  let currentRow = rowCount - 1

  /** –ø–∏—Å–∞—Ç—å –Ω–∏–∂–µ —ç—Ç–æ–π —Å—Ç—Ä–æ–∫–∏*/
  for (let i = 0; i < (rowCount * colCount); i++) {
    newArr.push(matrix[currentRow][currentCol]);
    if (currentRow === 0) {
      currentRow = rowCount;
      currentCol ++
      result.push(newArr);
      newArr = [];
    }
    currentRow--
  }
  return result;
};


