const Root = ({ dashboardIds, rootId }) => {

  const [activeDashboardId, setId] = useState();

  const count = useRef(0);

  useEffect( () => {
    count.current +=1;
  });


    if (!rootId || !dashboardIds) {
    return null;
  }

  const setDashboardId = useCallback((id)=>{
        setId(id)
  },[id])


  return (
