      <h2>Active dashboard: {activeDashboardId ?? 'not choosen' }</h2>
      <span>Rerender count: {count}</span>
      {dashboardIds.map(id => (
        rootId && <Dashboard
                      key={id}
                      dashboardId={id}
                      onClick={(id) => setDashboardId(id)} 
                      rootId={rootId}
                      />
      ))}
    </>
  );
};

Root.propTypes = {
  dashboardIds: PropTypes.arrayOf(PropTypes.number).isRequired,
  rootId: PropTypes.number,
};

Root.defaultProps = {

};

