// Task: Fetch and Display Posts with Pagination

// Objective:
// Create a React application that fetches posts from the JSONPlaceholder API
// and displays them with pagination.

// Requirements:
// 1. Fetch Posts:
//    - Fetch posts from `https://jsonplaceholder.typicode.com/posts?_page=1&_limit=10`.
//    - Display the posts in a list format.
// 2. Pagination:
//    - Implement pagination to navigate through the list of posts.
// 3. Loading State:
//    - Show a loading indicator while fetching the posts.
// 4. Error Handling:
//    - Display an error message if the fetch request fails.

import "./styles.css";
import { useState, useEffect } from "react";

// const POSTS_API_URL =
//   "https://jsonplaceholder.typicode.com/posts?_page=1&_limit=10";

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(10);

  useEffect(() => {
    (async () => {
      setIsLoading(true);
      setError(null);
      try {
        const res = await fetch(
          `https://jsonplaceholder.typicode.com/posts?_page=${page}&_limit=10`
        );
        const postsData = await res.json();
        setPosts(postsData);
      } catch (err) {
        setError(err.message);
        setPosts([]);
      } finally {
        setIsLoading(false);
      }
    })();
  }, [page]);

  const hadlePrevPage = () => {
    setPage((prevPage) => Math.max(prevPage - 1, 1));
    console.log(page);
  };

  const hadleNextPage = () => {
    setPage((prevPage) => Math.min(prevPage + 1, totalPages));
    console.log(page);
  };

  return (
    <div>
      {loading && <p>Loading...</p>}
      {error && <p>{error}</p>}
      <ul>
        {posts.map((post) => {
          return <li key={post.id}>{post.title}</li>;
        })}
      </ul>
      <div className="pagination">
        <button onClick={hadlePrevPage}>Prev</button>
        <button onClick={hadleNextPage}>Next</button>
      </div>
    </div>
  );
};

export default function App() {
  return (
    <div className="app">
      <header className="header">
        <h1>Posts</h1>
      </header>

      <Posts />
    </div>
  );
}


