function countingSortDescending(arr) {
    if (arr.length == 0) return [];

// –ù–∞—Ö–æ–¥–∏–º –º–∏–Ω–∏–º—É–º –∏ –º–∞–∫—Å–∏–º—É–º
    let min = arr[0], max = arr[0];
    for (let num of arr) {
    if (num < min) min = num;
    if (num > max) max = num;
    }

    const count = new Array(max - min + 1).fill(0);

    // –ü–æ–¥—Å—á–∏—Ç—ã–≤–∞–µ–º –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –∫–∞–∂–¥–æ–≥–æ —á–∏—Å–ª–∞
    for (let num of arr) {
    count[num - min]++;
    }
}

// –°—Ç—Ä–æ–∏–º –æ—Ç—Å–æ—Ä—Ç–∏—Ä–æ–≤–∞–Ω–Ω—ã–π –º–∞—Å—Å–∏–≤ –ø–æ —É–±—ã–≤–∞–Ω–∏—é
const result = [];
for (let i = count.length - 1; i >= 0; i--) {
    while (count[i] > 0) {
    result.push(i + min);
    count[i]--;
    }
}
return result;
}

// –ü—Ä–∏–º–µ—Ä:
