 */
type HandlerType<T> = (value: T) => void

class Subscription<T> {
  subscriber: Set<HandlerType<T>> = new Set()
  subscribe(handler : HandlerType<T>) {
    this.subscriber.add(handler)
    return ()=> this.unsubscribe1(handler)
  }
  unsubscribe1(handler: HandlerType<T>) {
    this.subscriber.delete(handler )
  }
  next(value: T) {
    this.subscriber.forEach(handler => handler(value))
  }
}

const sub = new Subscription<string>();

