import styles from './styles.module.css';
import Loader from "../../components/loader"
import {Api} from "../../api"

interface Props {
  userId: number;
  unmount: () => void;
}

class UserContainer extends Component<Props> {  
  constructor(props) {
    super(props)
    this.state = {
      user: null,
      isLoading: false, 
      error: null
    }
    this.api = new Api()
  }

    componentDidMount() {
      this.loadUser
    }
    loadUser = async () => {
      try {
        const user = await this.api.getUser(this.props.userId)
