import React, { useState, useEffect, useCallback } from "react";
import sendData from "data";
import bigComputations from "bigComputations";

import webp from "./car.webp";
import gif from "./car.gif";
import png from "./car.png";

const ReviewCarousel = (props) => {
  const [data, setData] = useState(bigComputations(props.argument));
  const [items] = useState([{ id: 1 }, { id: 2 }, { id: 3 }]);

  useEffect(() => {
    const handleClick = () => {
      setData((prevData) => prevData * 5);
    };

    document.addEventListener("click", handleClick);
    return () => {
      document.removeEventListener("click", handleClick);
    };
  }, []);

  const handleClick = useCallback((id) => {
    sendData(data, id);
  }, [data]);

  return (
    <>
      {items.map((item) => (
        <img
          key={item.id}
          src={webp}
          alt={`Item ${item.id}`}
          onClick={() => handleClick(item.id)}
        />
      ))}
    </>
  );
};

export default ReviewCarousel;

