  useEffect(() => {
    const loadData = async () => {
      try {
        setLoading(true);
        const [usersData, postsData] = await Promise.all([fetchUsers(), fetchPosts()]);

        const usersWithPosts = usersData.map(user => ({
          ...user,
          posts: postsData.filter(post => post.userId === user.id)
        }));

        setUsers(usersWithPosts);
