export default function App() {
  const [counter, setCounter] = useState(0);

  const increment = () => {
    setCounter(counter + 1);
  };

  const decrement = () => {
    setCounter(counter - 1);
  };

  const clear = () => {
    setCounter(0);
  };

  return (
    <div className="App">
      <CounterView counter={counter} />
      <Actions increment={increment} decrement={decrement} clear={clear} />
    </div>
  );
