function get(url, limit) {
    let counter = 0;
    const attempt = () => {
        fetch(url).then((response) => {
            if (!response.ok) {
                throw new Error("Request failed with status " + response.status);
            }
            return response.json();
        })
        .catch((err) => {
            if (counter < limit) {
                counter++;
                attempt();
            } else {
                return new Error(err);
            }
        });
    }
    return attempt(limit);
}
