    this.head = 0; 
    this.tail = 0; 
    this.size = 0; 
  }

  write(item) {
    if (this.size < this.max_size) {
      this.size++;
    } else {

      this.head = (this.head + 1) % this.max_size;
    }

    this.buffer[this.tail] = item;
    this.tail = (this.tail + 1) % this.max_size;
  }

  read() {
    if (this.size === 0) return null; 

    const item = this.buffer[this.head];
    this.head = (this.head + 1) % this.max_size;
    this.size--;
    return item;
  }

  getSize() {
    return this.size;
  }
