function lengthOfLongestSubstring(s) {
    let maxValue = 0;

    for (let i = 0; i < s.length; i++) {
        const set = new Set();

        for (let j = i; j < s.length; j++) {
            if (set.has(s[j])) {
               break;
             }

            set.add(s[j]);
            maxValue = Math.max(maxValue, j - i + 1);
        }
    }

    return maxValue;
}



