function rangeExtraction(list) {
  let start = list[0];
  let end = start;
  let result = [];
  let itemsArr = [start];
  for (let i = 1; i < list.length; i++) {
    const currentNum = list[i];
    if (Math.abs(Math.abs(end) - Math.abs(currentNum)) === 1) {
      itemsArr.push(currentNum);
      end = currentNum;
    } else {
      if (itemsArr.length >= 3) {
        result.push(`${itemsArr[0]}-${itemsArr.at(-1)}`);
      } else {
        result.push(itemsArr);
      }
      start = currentNum;
      end = start;
      itemsArr = [currentNum];
    }
  }
  if (itemsArr.length >= 3) {
    result.push(`${itemsArr[0]}-${itemsArr.at(-1)}`);
  } else {
    result.push(itemsArr);
  }
  return result.flat().join();
}

