function groupByCategory(items) {
    return items.reduce((acc, item) => {
        // –ï—Å–ª–∏ –∫–∞—Ç–µ–≥–æ—Ä–∏—è —É–∂–µ –µ—Å—Ç—å –≤ –∞–∫–∫—É–º—É–ª—è—Ç–æ—Ä–µ, –¥–æ–±–∞–≤–ª—è–µ–º —ç–ª–µ–º–µ–Ω—Ç –≤ –º–∞—Å—Å–∏–≤
        if (acc[item.category]) {
            acc[item.category].push(item);
        } else {
            // –ï—Å–ª–∏ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏ –Ω–µ—Ç, —Å–æ–∑–¥–∞–µ–º –Ω–æ–≤—ã–π –º–∞—Å—Å–∏–≤ —Å —ç—Ç–∏–º —ç–ª–µ–º–µ–Ω—Ç–æ–º
            acc[item.category] = [item];
        }
        return acc;
