import { useState, useEffect, useRef, useCallback } from "react";
import "./styles.css";

export default function App() {
  const [harData, setHarData] = useState({
    log: {
      version: "1.2",
      creator: { name: "React HAR Editor", version: "1.0" },
      entries: [
        {
          request: { url: "https://example.com", method: "GET" },
          response: { status: 200, statusText: "OK" },
        },
      ],
    },
  });

  const [error, setError] = useState(null);
  const [value, setValue] = useState("");
  const saveTimeout = useRef(null);

  const addNewEntry = useCallback(() => {}, []);

  const handleInputChange = (index, field, value) => {
    harData.log.entries[index].request[field] = value;

    setHarData(value);
  };

  const downloadHarFile = () => {
    const blob = new Blob([JSON.stringify(harData, null, 2)], {
      type: "application/json",
    });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "edited.har";
    link.click();
  };

  useEffect(() => {
    if (saveTimeout.current) {
      clearTimeout(saveTimeout.current);
    }

    saveTimeout.current = window.setTimeout(() => {
      console.log("Autosave triggered");
    }, 1000);
  }, []);

  return (
    <div style={{ padding: "20px", maxWidth: "800px", margin: "0 auto" }}>
      <h1>HAR File Editor</h1>
      {error && <p style={{ color: "red" }}>{error}</p>}

      <h2>Entries</h2>
      <ul style={{ maxHeight: "300px", overflowY: "scroll", padding: "0" }}>
        {harData.log.entries.map((entry) => (
          <li
            style={{
              marginBottom: "15px",
              padding: "10px",
              border: "1px solid #ddd",
              borderRadius: "5px",
              listStyle: "none",
            }}
          >
            <label>
              URL:
              <input
                type="text"
                value={entry.request.url || ""}
                onChange={(e) =>
                  handleInputChange(index, "url", e.target.value)
                }
                style={{
                  width: "100%",
                  padding: "5px",
                  marginBottom: "10px",
                  border: "1px solid #ccc",
                }}
              />
            </label>
            <label>
              Method:
              <select
                value={entry.request.method || "GET"}
                onChange={(e) =>
                  handleInputChange(index, "method", e.target.value)
                }
                style={{
                  width: "100%",
                  padding: "5px",
                  marginBottom: "10px",
                  border: "1px solid #ccc",
                }}
              >
                <option value="GET">GET</option>
                <option value="POST">POST</option>
                <option value="PUT">PUT</option>
                <option value="DELETE">DELETE</option>
              </select>
            </label>
          </li>
        ))}
      </ul>
      <button
        onClick={addNewEntry}
        style={{
          marginTop: "10px",
          padding: "10px 15px",
          backgroundColor: "green",
          color: "white",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        Add New Entry
      </button>
      <button
        onClick={downloadHarFile}
        style={{
          marginTop: "10px",
          marginLeft: "10px",
          padding: "10px 15px",
          backgroundColor: "blue",
          color: "white",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        Download HAR
      </button>
    </div>
  );
}

